package run.acloud.api.configuration.vo;

import com.fasterxml.jackson.annotation.JsonIgnore;
import com.fasterxml.jackson.core.JsonProcessingException;
import io.swagger.v3.oas.annotations.media.Schema;
import lombok.Getter;
import lombok.Setter;
import org.apache.commons.lang.StringUtils;
import run.acloud.api.auth.enums.LanguageType;
import run.acloud.api.auth.vo.UserVO;
import run.acloud.api.configuration.enums.AccountType;
import run.acloud.api.configuration.enums.UserAuthType;
import run.acloud.commons.util.ObjectMapperUtils;
import run.acloud.commons.vo.HasUseYnVO;

import java.io.Serializable;
import java.util.List;

@Getter
@Setter
@Schema(description = "계정 모델")
public class AccountVO extends HasUseYnVO implements Serializable {
    private static final long serialVersionUID = -2187706228741775857L;

    @Schema(title = "계정 번호", requiredMode = Schema.RequiredMode.REQUIRED)
    private Integer accountSeq;

    @Schema(title = "계정 유형", allowableValues = {"CCO","ENT","OEM"}, requiredMode = Schema.RequiredMode.REQUIRED, example="CCO")
    private AccountType accountType = AccountType.CCO;

    @Schema(title = "계정 이름", requiredMode = Schema.RequiredMode.REQUIRED, example="Acorn Test Account")
    private String accountName;

    @Schema(title = "조직명", requiredMode = Schema.RequiredMode.REQUIRED, example="Acloud Development Team")
    private String organizationName;

    @Schema(title = "설명", example="아 to the 콘")
    private String description;

//    @Schema(title = "로고 이미지", example="data:image/png;base64, ")
    @Schema(title = "로고 이미지", example="null")
    private String logoImage;

    @Schema(title = "라이센스 키", example="todo")
    private String licenseKey;

    @Schema(title = "계정 등급 정보")
    private AccountGradeVO accountGrade;

    @Schema(title = "기본 언어", requiredMode = Schema.RequiredMode.REQUIRED, example="en")
    private LanguageType baseLanguage = LanguageType.en;

    @Schema(title = "계정 사용자 번호")
    private Integer accountUserSeq;

    @Schema(title = "계정 사용자 번호")
    private String accountUserId;

    @Schema(title = "계정 사용자 정보(for add, edit)", example = "null", accessMode = Schema.AccessMode.READ_ONLY)
    private UserVO accountUser;

    @Schema(title = "계정 사용자번호 목록")
    private List<Integer> accountUserSeqs;

    @Schema(title = "계정 사용자 정보(for list)", example = "[]")
    private List<UserVO> accountUsers;

    @Schema(title = "providerAccount Count")
    private Integer providerAccountCount;

    @Schema(title = "providerAccount 정보")
    private List<ProviderAccountVO> providerAccounts;

    @Schema(title = "계정을 사용할 수 있는 사용자 Count")
    private Integer userCount;

    @Schema(title = "계정을 사용할 수 있는 사용자번호 목록")
    private List<Integer> userSeqs;

    @Schema(title = "계정을 사용할 수 있는 사용자 목록", example = "[]")
    private List<UserVO> users;

    @Schema(title = "계정에 연결된 Workspace Count")
    private Integer serviceCount;

    @Schema(title = "계정 코드")
    private String accountCode;

    @Schema(title = "고객명")
    private String customerName;

    @Schema(title = "고객이메일")
    private String customerEmail;

    @Schema(title = "고객주소")
    private String customerAddress;

    @Schema(title = "사용자 인증 방식")
    private UserAuthType userAuthType;

    @Schema(title = "Application ID for Azure Active Directory")
    private String appId;

    @Schema(title = "동시 세션 허용 여부( Y : 허용, N : 미허용 )")
    private String concurrentSessionYn;

    @Schema(title = "레지스트리 pull 전용 사용자ID")
    @JsonIgnore
    private String registryDownloadUserId;

    @Schema(title = "레지스트리 pull 전용 사용자PW")
    @JsonIgnore
    private String registryDownloadUserPassword;

    /** R4.1.1 : 추가 : 2020.03.11 **/
    @Schema(title = "퍼블릭 클라우드의 Access Key / Secret 정보와 클러스터의 맵핑 목록")
    private List<ClusterProviderVO> clusterProviders;

    @Schema(title = "Account내의 클러스터들중 Provider의 AccessKey가 연결된 클러스터 목록", example = "[]")
    private List<ClusterVO> clusters;

    /** R4.4.0 : 추가 2020.06.17 **/
    @Schema(title = "Account내의 서비스 레지스트리 목록")
    private List<ServiceRegistryVO> projects;

    @Schema(title = "플랫폼의 등록된 레지스트리 정보")
    private AccountRegistryVO accountRegistry;

    @Schema(title = "플랫폼 구성 정보")
    private AccountConfigVO accountConfig;

    public AccountConfigVO getAccountConfig() {
        if (StringUtils.isNotBlank(this.accountConfigJson)) {
            try {
                this.accountConfig = ObjectMapperUtils.getMapper().readValue(this.accountConfigJson, AccountConfigVO.class);
            } catch (JsonProcessingException e) {
                throw new RuntimeException(e);
            }
        } else {
            return this.accountConfig = new AccountConfigVO();
        }

        return this.accountConfig;
    }

    @Schema(title = "플랫폼 구성 정보 json", hidden = true)
    @JsonIgnore
    private String accountConfigJson;

    public String getAccountConfigJson() {
        if (this.accountConfig != null) {
            try {
                this.accountConfigJson = ObjectMapperUtils.getMapper().writeValueAsString(this.accountConfig);
            } catch (JsonProcessingException e) {
                throw new RuntimeException(e);
            }
        } else {
            this.accountConfigJson = null;
        }

        return this.accountConfigJson;
    }
}